import React, { Component } from 'react'
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input
} from 'reactstrap'
import { connect } from 'react-redux'
import { addCharacter } from '../actions/characterActions'
import PropTypes from 'prop-types'

class CharacterModal extends Component {
  state = {
    // Modal is closed when page loads
    modal: false,
    charName: ''
  }

  // Toggle the modal visibility
  toggle = () => {
    this.setState({
      modal: !this.state.modal
    })
  }

  // Change the state of the text input variables for the POST requests
  onChange = e => {
    // Update the name state to show in the text box and have a name to submit
    this.setState({
      [e.target.charName]: e.target.value
    })
  }

  onSubmit = e => {
    // Prevent the page from refreshing on submit
    e.preventDefault()

    const newCharacter = {
      name: this.state.name
    }

    // Add new character to the database
    this.props.addCharacter(newCharacter)

    // Close the modal
    this.toggle()
  }

  render() {
    return (
      <div>
        <Button
          color='dark'
          style={{ marginBottom: '2rem' }}
          onClick={this.toggle}
        >
          New Character
        </Button>
        <Modal>
          <ModalHeader></ModalHeader>
          <ModalBody>
            <Form>
              <FormGroup>
                <Label></Label>
                <Input />
              </FormGroup>
            </Form>
          </ModalBody>
        </Modal>
      </div>
    )
  }
}

const mapStateToProps = state => ({})

export default connect(
  mapStateToProps,
  { addCharacter }
)(CharacterModal)
