import React, { Component } from 'react'
import { connect } from 'react-redux'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import {
  Container,
  ListGroup,
  ListGroupItem,
  Spinner,
  Button,
  Row,
  Col
} from 'reactstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAsterisk } from '@fortawesome/free-solid-svg-icons'
import { getCharacters, deleteCharacter } from '../actions/characterActions'

class CharacterList extends Component {
  componentDidMount() {
    this.props.getCharacters()
  }

  static propTypes = {
    getCharacters: PropTypes.func.isRequired,
    deleteCharacter: PropTypes.func.isRequired,
    characters: PropTypes.array.isRequired,
    loading: PropTypes.bool
  }

  onDeleteClick = id => {
    this.props.deleteCharacter(id)
  }

  render() {
    const { characters, loading } = this.props
    return (
      <Container>
        {!loading ? (
          <ListGroup>
            {characters.map(({ _id, name }) => (
              <ListGroupItem key={_id}>{name}</ListGroupItem>
            ))}
          </ListGroup>
        ) : (
          <Container className='spinner-container'>
            <Spinner
              style={{ width: '3rem', height: '3rem' }}
              color='secondary'
            />
          </Container>
        )}
      </Container>
    )
  }
}

const mapStateToProps = state => ({
  characters: state.character.characters,
  loading: state.character.loading
})

export default connect(
  mapStateToProps,
  { getCharacters, deleteCharacter }
)(CharacterList)
